import * as React from "react"
import { PropertyControls, addPropertyControls, ControlType } from "framer"

const iconStyle = [
    "ios7",
    "win10",
    "metro",
    "androidL",
    "android",
    "material",
    "material-rounded",
    "material-outlined",
    "color",
    "office",
    "ultraviolet",
    "dotty",
    "nolan",
    "dusk",
]
const iconTitles = [
    "iOS",
    "Windows 10",
    "Windows Metro",
    "Material Design Filled",
    "Android Ice Cream",
    "Material",
    "Material Rounded",
    "Material Outlined",
    "Flat Color",
    "Office",
    "Ultraviolet",
    "Dotted",
    "Gradient Line",
    "Dusk",
]

export class Icons8 extends React.Component<IconProps> {
    static propertyControls: PropertyControls = {
        search: {
            type: ControlType.String,
            title: "Search Icon",
        },

        color: {
            type: ControlType.String,
            title: "Icon Color",
        },
        style: {
            type: ControlType.Enum,
            options: iconStyle,
            optionTitles: iconTitles,
            title: "Icon Style",
        },
    }

    static defaultProps = {
        search: "icons8",
        width: 96,
        height: 96,
        style: "win10",
        color: "#FFFFFF",
    }

    // Icon
    render() {
        // Declare props
        const search = this.props.search
        const style = this.props.style
        const color = this.props.color
        return <IconItem search={search} color={color} style={style} />
    }
}
// Renders IconItem
class IconItem extends React.Component<IconItemProps> {
    render() {
        const style = this.props.style
        const search = this.props.search
        const color = (this.props.color || "").replace("#", "")

        return (
            <div>
                <img
                    src={`https://png.icons8.com/${style}/${search}/${color}/256`}
                    style={icon}
                />
            </div>
        )
    }
}

//Default Styles Override
const icon: React.CSSProperties = {
    background: "transparent",
    height: "auto",
    width: "100%",
}

//Define Props type
interface IconProps {
    search: string
    direction: string
    iconLayout: string
    color: string
    style: string
}

interface IconItemProps {
    search: string
    iconLayout: string
    style: string
    color: string
}
